mapper.select:
select
*
from SYS_ATTACH_CATEGORY_B

    1 = 1

        AND SOURCE_TYPE = #{sourceType,jdbcType=VARCHAR}


        AND PARENT_CATEGORY_ID = #{parentCategoryId,jdbcType=VARCHAR}


        and STATUS = #{status,jdbcType=VARCHAR}

select b.category_id, t.category_name, b.leaf_flag, t.description, b.status, b.parent_category_id, b.source_type, b.allowed_file_type, b.allowed_file_size, b.category_path, b.is_unique from SYS_ATTACH_CATEGORY_B B LEFT JOIN SYS_ATTACH_CATEGORY_TL T ON (B.category_id = T.category_id AND t.lang = #{request.locale,jdbcType=VARCHAR}) where b.category_id in
#{id}
order by b.category_id
