mapper.select:
SELECT SCVB.CODE_VALUE_ID, SCVB.CODE_ID, SCVB.VALUE, SCVT.MEANING, SCVT.DESCRIPTION, SCVB.TAG, SCVB.ORDER_SEQ, SCVB.ENABLED_FLAG, SCVB.PARENT_CODE_VALUE_ID, SCVB.PARENT_CODE_VALUE, SCVB.OBJECT_VERSION_NUMBER, SCVT2.MEANING PARENT_CODE_VALUE_MEANING, SCVB.ATTRIBUTE_CATEGORY, SCVB.ATTRIBUTE1, SCVB.ATTRIBUTE2, SCVB.ATTRIBUTE3, SCVB.ATTRIBUTE4, SCVB.ATTRIBUTE5, SCVB.ATTRIBUTE6, SCVB.ATTRIBUTE7, SCVB.ATTRIBUTE8, SCVB.ATTRIBUTE9, SCVB.ATTRIBUTE10, SCVB.ATTRIBUTE11, SCVB.ATTRIBUTE12, SCVB.ATTRIBUTE13, SCVB.ATTRIBUTE14, SCVB.ATTRIBUTE15 FROM SYS_CODE_VALUE_B SCVB LEFT JOIN SYS_CODE_VALUE_TL SCVT ON ( SCVB.CODE_VALUE_ID = SCVT.CODE_VALUE_ID AND SCVT.LANG = #{request.locale,jdbcType=VARCHAR} ) LEFT JOIN SYS_CODE_VALUE_TL SCVT2 ON ( SCVB.PARENT_CODE_VALUE_ID = SCVT2.CODE_VALUE_ID AND SCVT2.LANG = #{request.locale,jdbcType=VARCHAR} )
AND SCVB.CODE_ID = #{codeId}
ORDER BY SCVB.ORDER_SEQ ASC

SELECT SCVB.CODE_VALUE_ID, SCVB.VALUE, SCVB.MEANING
  FROM SYS_CODE_VALUE_B SCVB
WHERE SCVB.CODE_VALUE_ID = #{codeValueId,jdbcType=DECIMAL}
