select COALESCE(sum(il.yeild),0) as yeild,COALESCE(sum(il.work_scrap),0) as workScrap,COALESCE(sum(il.row_scrap),0) as rowScrap from confirmation_input_log il,confirmation_result r,confirmation_log l,sap_afko ao,sap_afvc ac
il.id= r.input_id AND l.ref_id = il.id AND r.is_reversed ='0' AND l.tran_type = '0' AND l.msgty = 'S' and il.operation = '0010' AND ao.aufnr = il.orderno AND ao.aufpl = ac.aufpl and (ac.flag = '' or ac.flag is null) AND ao.auart in('YZ01','YZ03','YZ04')
AND il.plant = #{plant}
AND ac.arbpl = #{arbpl}
AND il.attr4 = #{attr4}
AND il.attr6 = #{attr6}

SELECT COALESCE(sum(il.yeild),0) from confirmation_input_log il,confirmation_result r,confirmation_log l,sap_afko ao,sap_afvc ac
il.id= r.input_id AND l.ref_id = il.id AND r.is_reversed ='0' AND l.tran_type = '0' AND l.msgty = 'S' and il.operation = '0010' AND ao.aufnr = il.orderno AND ao.aufpl = ac.aufpl and (ac.flag = '' or ac.flag is null) AND ao.auart in('YZ01','YZ03','YZ04')
AND ac.arbpl = #{arbpl}
AND il.CREATION_DATE >= #{postingDateAfter}
AND #{postingDateBefore} >= il.CREATION_DATE

SELECT *
FROM confirmation_input_log
WHERE id=#{id}

