il.id= r.input_id AND il.CREATED_BY = u.user_id AND l.ref_id = il.id AND r.is_reversed ='0' AND l.tran_type = '0' AND l.msgty = 'S'
and il.plant = #{plant}
and il.orderno = #{orderno}
