SELECT sum(il.yeild) as yeild, sum(il.work_scrap) as work_scrap, sum(il.row_scrap) as row_scrap FROM confirmation_input_log il,confirmation_result r,sys_user u,confirmation_log l,sap_afko af,sap_afvc afvc
il.id= r.input_id and il.orderno = af.aufnr and af.aufpl = afvc.aufpl and il.operation = afvc.vornr AND il.CREATED_BY = u.user_id AND l.ref_id = il.id AND l.ref_id = il.id AND r.is_reversed ='0' AND l.tran_type = '0' AND l.msgty = 'S' and il.plant = '1001' and il.operation = '0010' AND ( afvc.flag is null or afvc.flag = '' ) and af.auart in ('YZ01','YZ03','YZ04')
and r.material = #{matnr}
and afvc.arbpl = #{arbpl}
and il.attr6 = #{attr6}
and il.attr4 = #{attr4}
