mapper.select:
select xh.werks,xh.matnr,ma.maktx,xh.lgort,st.lgobe,xh.zxhbar,xh.chargkc,xh.zmnum,
xh.menge,xh.zsxwc,xh.zscbc,xh.LAST_UPDATE_DATE as lastUpdateDate2
from sap_xhcard xh,sap_marc ma,sap_t001l st/*,sap_cardh ch,confirmation_input_log l*/

    xh.matnr = ma.matnr
    AND xh.lgort = st.lgort
    AND xh.zxhzt = '7'
    /*and xh.aufnr = ch.aufnr and xh.zxhnum = ch.zxhnum
    and ch.zpgdbar = l.dispatch
    and l.operation = "0010"*/

        AND xh.werks = #{werks}


        AND xh.lgort = #{lgort}


        AND xh.matnr = #{matnr}


        AND xh.zxhbar = #{zxhbar}


        AND xh.chargkc like concat(#{chargkc},'%')


        AND xh.zscbc = #{zscbc}




        AND (xh.zsxwc = '' or xh.zsxwc is null)

mapper.update:
update sap_xhcard set WERKS = #{werks},
                      MATNR = #{matnr},
                      CHARG = #{charg},
                      ZXHNUM = #{zxhnum},
                      ZXHZT = #{zxhzt},
                      ZXHZT2 = #{zxhzt2},
                      LGORT = #{lgort},
                      MENGE = #{menge},
                      MEINS = #{meins},
                      ZXHWZ = #{zxhwz},
                      ZXHBAR = #{zxhbar},
                      ZJYY = #{zjyy},
                      ZSCBC = #{zscbc},
                      ZSCX = #{zscx},
                      ZMNUM = #{zmnum},
                      ZSCTPTM = #{zsctptm},
                      ZTXT = #{ztxt},
                      ZBQBD = #{zbqbd},
                      CHARGKC = #{chargkc}

                          ,CREATION_DATE = #{creationDate}


                          ,CREATED_BY = #{createdBy}


                          ,LAST_UPDATE_DATE = #{lastUpdatedDate}


                          ,LAST_UPDATED_BY = #{lastUpdatedBy}



                          WERKS = #{werks}
                          and MATNR = #{matnr}
                          and CHARG = #{charg}
                          and ZXHNUM = #{zxhnum}

mapper.delete:
DELETE FROM sap_xhcard

    WERKS = #{werks}
    and MATNR = #{matnr}
    and CHARG = #{charg}
    and ZXHNUM = #{zxhnum}
    and AUFNR = #{aufnr}

mapper.select:
SELECT
DISTINCT
*
FROM sap_xhcard

    1 = 1

      and ZXHBAR = #{zxhbar}


      AND MATNR = #{matnr}


     AND  AUFNR = #{aufnr}


    AND  LGORT = #{lgort}


    AND ZSCBC = #{zscbc}


    AND ZMNUM = #{zmnum}


        and CREATION_DATE = #{datestr}

    ORDER BY WERKS,MATNR,AUFNR,ZXHBAR ASC

SELECT * FROM  sap_xhcard

    MATNR = #{matnr}
    and LGORT = #{lgort}
    and ( ZSXWC IS  NULL OR ZSXWC = '')

ORDER BY CHARGKC ASC;

SELECT * FROM  sap_xhcard

    MATNR = #{matnr}
    and LGORT = #{lgort}
    and ( ZSXWC IS  NULL OR ZSXWC = '')
    and ZXHZT = '7'

ORDER BY CHARGKC ASC;

select MAX(CHARG) FROM sap_xhcard

    MATNR = #{matnr}
    and CHARG like #{charg}

mapper.update:
UPDATE sap_xhcard SET ZSXWC = #{zsxwc}

    WERKS = #{werks}
    and MATNR = #{matnr}
    and CHARG = #{charg}
    and ZXHNUM = #{zxhnum}

mapper.select:
select sap_xhcard.ZXHBAR as bar, sap_xhcard.WERKS as werks, sap_xhcard.MATNR as matnr, sap_xhcard.ZXHNUM as zxhnum, sap_xhcard.AUFNR as aufnr, sap_xhcard.ZMNUM as zmnum, sap_xhcard.CHARGKC as chargkc, sap_xhcard.ZSXWC as zsxwc, sap_xhcard.LGORT as lgort, sap_xhcard.MENGE as menge, ( select sum(ZSXNUM) from zwipq WHERE ZXHBAR = bar ) AS zsxnum, ( select sum(ZSXNUM) from zwipq where ZXHBAR = bar and ZREMADE = 0 AND ZZXKL = 0 AND ZQJKL = 0 ) AS dlsum, ( select PKG_LINE_ID FROM zwipq where ZXHBAR = bar LIMIT 1) as pkgLineId, ( select DESCRIPTIONS FROM wip_lines WHERE LINE_ID = pkgLineId) as descriptions, ( select sum(SMENGE) FROM sap_ztbc0018 where ZXHBAR = bar) AS smenge, ( select sum(BMENGE) FROM sap_ztbc0018 where ZXHBAR = bar) AS bmenge, ( select CREATION_DATE FROM zwipq where ZXHBAR = bar ORDER by CREATION_DATE ASC LIMIT 1) AS attr1, sap_cardh.ZPGDBAR as zpgdbar, sap_cardh.SFFLG as sfflg, sap_marc.MAKTX as maktx from sap_xhcard LEFT JOIN sap_cardh on ( sap_cardh.AUFNR = sap_xhcard.AUFNR AND sap_cardh.MATNR = sap_xhcard.MATNR AND sap_cardh.ZXHNUM = sap_xhcard.ZXHNUM ) LEFT JOIN sap_marc on (sap_marc.MATNR = sap_xhcard.MATNR)
1 = 1
and sap_xhcard.WERKS = #{werks}
and sap_xhcard.LGORT = #{lgort}
and sap_xhcard.ZXHBAR = #{zxhbar}
and sap_xhcard.SFFLG = #{sfflg}
and ( sap_xhcard.ZSXWC IS NULL OR sap_xhcard.ZSXWC = '' )
and sap_xhcard.MATNR = #{matnr}

select * from sap_xhcard

    WERKS = #{werks}
    and AUFNR = #{aufnr}
    and ZXHNUM = #{zxhnum}
