mapper.select:
SELECT ,
FROM SYS_TASK_EXECUTION TE LEFT JOIN SYS_TASK_DETAIL_B TD ON TD.TASK_ID=TE.TASK_ID LEFT OUTER JOIN SYS_TASK_DETAIL_TL TTL ON (TTL.TASK_ID = TE.TASK_ID AND TTL.LANG = #{request.locale,jdbcType=VARCHAR,javaType=java.lang.String})
TE.USER_ID = #{request.userId}
AND TE.EXECUTION_NUMBER IS NOT NULL
AND TE.STATUS = #{taskExecution.status}
AND TE.EXECUTION_NUMBER LIKE concat('%', concat(#{taskExecution.executionNumber,jdbcType=VARCHAR}, '%'))
AND TD.NAME LIKE concat('%', concat(#{taskExecution.name,jdbcType=VARCHAR}, '%'))
AND TD.TYPE = #{taskExecution.type}
ORDER BY TE.${_colName} ${taskExecution.sortorder}
ORDER BY TE.EXECUTION_ID DESC

SELECT , ,
FROM SYS_TASK_EXECUTION TE LEFT OUTER JOIN SYS_TASK_EXECUTION_DETAIL TED ON TE.EXECUTION_ID = TED.EXECUTION_ID LEFT OUTER JOIN SYS_TASK_DETAIL_B TD ON TD.TASK_ID = TE.TASK_ID LEFT OUTER JOIN SYS_TASK_DETAIL_TL TTL ON ( TTL.TASK_ID = TE.TASK_ID AND TTL.LANG = #{request.locale,jdbcType=VARCHAR,javaType=java.lang.String})
TE.EXECUTION_ID = #{executionId}

mapper.update:
UPDATE SYS_TASK_EXECUTION
SET STATUS = #{status}

    , START_TIME = current_timestamp


    , END_TIME = current_timestamp

WHERE
  EXECUTION_ID = #{executionId}

UPDATE SYS_TASK_EXECUTION
SET STATUS = #{aftStatus}
WHERE
PARENT_ID = #{executionId} AND STATUS = #{befStatus}
